+import Header from "./Header";
+import Footer from "./Footer";
+import { supabase } from "../../../supabaseConfig";
+import { useState, useEffect } from "react";
+
+const Layout = ({ children }) => {
+  const [user, setUser] = useState(null);
+
+  useEffect(() => {
