  display: grid;
  gap: 2rem;
  justify-content: center;
  grid-template-columns: repeat(auto-fit, minmax(12rem, 1fr));
}

.site-footer-logo {
  width: 7rem;
}

.site-footer h4 {
  text-transform: uppercase;
  letter-spacing: 1px;
  margin-bottom: 0.75rem;
  font-size: 1.1rem;
}

.site-footer a {
  color: #ffffff;
  transition: color 0.3s ease;
}

.site-footer a:hover,
.site-footer a:focus {
  color: #f4ce14;
}

.site-footer-nav ul {
  list-style-type: none;
  padding: 0;
}

.site-footer-nav li {
  margin-bottom: 0.5rem;
}

