.site-footer-contact p {
  margin-bottom: 0.5rem;
  display: flex;
  align-items: center;
}

.site-footer-social svg {
  margin-right: 1rem;
  transition: transform 0.3s ease;
}

.site-footer-social svg:hover {
  transform: scale(1.1);
}

