commits.content:
.hero {
  padding-top: 1.875rem;
  background-color: #495e57;
}

.hero .container {
  grid-template-columns: 1fr 1fr;
  gap: 3.5rem;
}

.hero-information {
  grid-column: 1;
}

.hero-information h1 {
  padding-top: 1.875rem;
  color: #f4ce14;
  font-size: 3.5rem;
  height: 2.5rem;
  line-height: 2.5rem;
}

.hero-information h2 {
  padding-top: 1.875rem;
  color: #ffffff;
  font-size: 2.25rem;
  height: 2.25rem;
  line-height: 2.25rem;
}

.hero-information p {
  padding-top: 1.875rem;
  margin-top: 1.5rem;
  color: #ffffff;
  font-size: 1.125rem;
}

.hero-information .button-primary {
  display: inline-block;
  margin-top: 2rem;
}

.hero-information .button-primary:hover,
.hero-information .button-primary:focus {
  border-color: #f4ce14;
}

.hero-image {
  grid-column: 2;
  width: 100%;
  height: 24rem;
  margin-bottom: -4.375rem;
  justify-self: end;
  border-radius: 1rem;
  object-fit: cover;
}

@media (max-width: 792px) {
  .hero .container {
    grid-template-columns: 1fr;
    grid-template-rows: auto auto;
  }

  .hero-information {
    grid-row: 1;
  }

  .hero-image {
    grid-column: 1;
    grid-row: 2;
  }
}
