.auth-container {
    max-width: 400px;
    margin: auto;
    padding: 2rem;
    text-align: center;
    background: #fff;
    border-radius: 10px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
  }
  
  .auth-container h2 {
    color: #495e57;
  }
  
  .auth-container form {
    display: flex;
    flex-direction: column;
    gap: 1rem;
  }
  
  .auth-container input {
    width: 100%;
    padding: 10px;
    font-size: 1rem;
    border: 1px solid #ccc;
    border-radius: 5px;
  }
  
  .auth-container button {
    background: #f4ce14;
    color: black;
    font-size: 1rem;
    font-weight: bold;
    border: none;
    padding: 10px;
    cursor: pointer;
    border-radius: 5px;
    transition: background 0.3s;
  }
  
  .auth-container button:hover {
    background: #ffcc00;
  }
  
  .google-btn {
    background: #db4437;
    color: white;
    border: none;
    padding: 10px;
    margin-top: 10px;
    cursor: pointer;
    width: 100%;
    font-size: 1rem;
    border-radius: 5px;
  }
  
  .facebook-btn {
    background: #4267b2;
    color: white;
    border: none;
    padding: 10px;
    margin-top: 10px;
    cursor: pointer;
    width: 100%;
    font-size: 1rem;
    border-radius: 5px;
  }
