import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./confirmation.css"; // Import CSS for styling

const OrderConfirmation = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const orderDetails = location.state || {};

  return (
    <div className="confirmation-container">
      <h2>üéâ Order Confirmed!</h2>
      <p>Thank you for your order. Your delicious meal is on the way! üçΩÔ∏è</p>
      
      {/* Order Details */}
      <div className="order-summary">
        <h3>Order Summary</h3>
        <ul>
          {orderDetails.items?.map((item, index) => (
            <li key={index}>
              {item.title} - {item.quantity} x ${item.price.toFixed(2)}
            </li>
          ))}
        </ul>
        <p><strong>Total:</strong> ${orderDetails.total?.toFixed(2)}</p>
        <p><strong>Estimated Delivery:</strong> 30-40 minutes</p>
      </div>
      
      {/* Buttons */}
      <div className="confirmation-buttons">
        <button onClick={() => navigate("/")}>Back to Home</button>
        <button onClick={() => navigate("/orders")}>View Order</button>
      </div>
    </div>
  );
};

export default OrderConfirmation;
