import React from "react";
import { useCart } from "../context/CartContext";
// Import your menu items from your data file or define them inline
import menuItems from "../data/menuItems"; 
import "./orderOnline.css";

const OrderOnline = () => {
  const { addItem, totalPrice, cart } = useCart();

  return (
    <div className="order-container">
      <h1 className="page-title">Order Online</h1>
      <div className="menu-grid">
        {menuItems.map((item) => (
          <div key={item.id} className="menu-card">
            <img src={item.image} alt={item.name} className="menu-image" />
            <div className="menu-details">
              <h2 className="menu-name">{item.name}</h2>
              <p className="menu-desc">{item.description}</p>
              <div className="menu-footer">
                <span className="menu-price">${item.price.toFixed(2)}</span>
                <button className="add-btn" onClick={() => addItem(item)}>
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Cart Summary (optional) */}
      {cart.length > 0 && (
        <div className="cart-summary">
          <h2>Cart Summary</h2>
          <p>Total: ${totalPrice.toFixed(2)}</p>
          <a href="/checkout" className="checkout-btn">
            Checkout
          </a>
        </div>
      )}
    </div>
  );
};

export default OrderOnline;
