commits.content:
.checkout-container {
    max-width: 600px;
    margin: 20px auto;
    padding: 20px;
    background: #fff;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  }
  
  .checkout-items {
    margin-bottom: 20px;
  }
  
  .checkout-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
    border-bottom: 1px solid #ddd;
  }
  
  .item-details {
    flex: 1;
  }
  
  .item-controls {
    display: flex;
    align-items: center;
    gap: 10px;
  }
  
  .item-controls button {
    padding: 5px 10px;
    font-size: 16px;
    cursor: pointer;
  }
  
  .remove-btn {
    background: red;
    color: white;
    border: none;
    padding: 5px 10px;
    cursor: pointer;
    font-size: 14px;
  }
  
  .order-summary {
    padding: 15px;
    border-top: 1px solid #ddd;
  }
  
  .order-summary p, .order-summary h3 {
    margin: 5px 0;
  }
  
  .payment-btn {
    width: 100%;
    padding: 10px;
    background: #28a745;
    color: white;
    font-size: 16px;
    border: none;
    cursor: pointer;
    margin-top: 10px;
  }
