.confirmation-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    text-align: center;
    padding: 20px;
    background-color: #f8f9fa;
  }
  
  .confirmation-card {
    background: #fff;
    padding: 30px;
    border-radius: 10px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    max-width: 400px;
    width: 100%;
  }
  
  .confirmation-card h2 {
    color: #2c3e50;
    font-size: 24px;
    margin-bottom: 10px;
  }
  
  .confirmation-card p {
    font-size: 16px;
    color: #555;
    margin-bottom: 15px;
  }
  
  .order-details {
    text-align: left;
    width: 100%;
    margin-top: 15px;
    padding: 10px;
    border-radius: 5px;
    background: #ecf0f1;
  }
  
  .order-details p {
    margin: 5px 0;
  }
  
  .home-btn {
    margin-top: 20px;
    padding: 10px 20px;
    background: #ff7f50;
    color: #fff;
    font-size: 16px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    transition: background 0.3s;
  }
  
  .home-btn:hover {
    background: #e67348;
  }
  
  /* Responsive */
  @media (max-width: 480px) {
    .confirmation-card {
      max-width: 90%;
      padding: 20px;
    }
  
    .confirmation-card h2 {
      font-size: 20px;
    }
  
    .confirmation-card p {
      font-size: 14px;
    }
  
    .home-btn {
      font-size: 14px;
      padding: 8px 16px;
    }
  }
