/* Container styling */
.order-container {
    max-width: 1200px;
    margin: 2rem auto;
    padding: 0 1rem;
  }
  
  /* Page Title */
  .page-title {
    text-align: center;
    font-size: 2rem;
    color: #495e57;
    margin-bottom: 1.5rem;
  }
  
  /* Grid Layout for Menu Items */
  .menu-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
  }
  
  /* Card Styling */
  .menu-card {
    background: #fff;
    border: 1px solid #e0e0e0;
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 4px 8px rgba(0,0,0,0.05);
    display: flex;
    flex-direction: column;
    transition: transform 0.3s ease;
  }
  
  .menu-card:hover {
    transform: translateY(-5px);
  }
  
  /* Image Styling */
  .menu-image {
    width: 100%;
    height: 150px;
    object-fit: cover;
  }
  
  /* Card Details */
  .menu-details {
    padding: 1rem;
    flex-grow: 1;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }
  
  .menu-name {
    font-size: 1.25rem;
    margin: 0.5rem 0;
    color: #333;
  }
  
  .menu-desc {
    font-size: 0.9rem;
    color: #777;
    flex-grow: 1;
  }
  
  .menu-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 1rem;
  }
  
  .menu-price {
    font-size: 1.1rem;
    font-weight: bold;
    color: #495e57;
  }
  
  /* Add to Cart Button */
  .add-btn {
    background: #f4ce14;
    border: none;
    padding: 8px 12px;
    border-radius: 4px;
    cursor: pointer;
    font-size: 0.9rem;
    font-weight: bold;
    transition: background 0.3s ease;
  }
  
  .add-btn:hover {
    background: #ffcc00;
  }
  
  /* Cart Summary Section */
  .cart-summary {
    margin-top: 2rem;
    padding: 1rem;
    border-top: 1px solid #e0e0e0;
    text-align: center;
  }
  
  .cart-summary h2 {
    margin-bottom: 0.5rem;
    color: #495e57;
  }
  
  .checkout-btn {
    display: inline-block;
    margin-top: 1rem;
    background: #f4ce14;
    color: black;
    text-decoration: none;
    padding: 10px 20px;
    border-radius: 4px;
    font-weight: bold;
    transition: background 0.3s ease;
  }
  
  .checkout-btn:hover {
    background: #ffcc00;
  }
