commits.content:
*,
*:after,
*:before {
  margin: 0;
  padding: 0;
  border: 0;
  outline: 0;
  box-sizing: border-box;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
}

html {
  scroll-behavior:smooth;
}

body {
  font-family: Karla, sans-serif;
  font-size: 1rem;
  line-height: 1.5;
  color: #495e57;
}

header {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height:fit-content;
  background-color: #ffffff;
  z-index: 999;
}

main {
  padding-top: 5rem;
  min-height: calc(100vh - 6.25rem - 18rem);
}

a {
  text-decoration: none;
  color: #495e57;
}

h1,
h2 {
  line-height: 1;
  font-family: "Markazi Text";
  font-weight: normal;
}

h1 {
  font-size: 3.5rem;
}

h2 {
  font-size: 2.5rem;
}

label {
  width: fit-content;
  cursor: pointer;
}

input,
select {
  width: 100%;
  height: 2.5rem;
  padding: 0 0.5rem;
  outline: none;
  border: 1px solid #e0e0e0;
  border-radius: 0.375rem;
  background: #ffffff;
  color: #495e57;
  cursor: pointer;
}

select {
  -webkit-appearance: none;
  appearance: none;
}

input:focus,
select:focus {
  border: 1px solid #495e57;
}

label {
  font-weight: bold;
}

button {
  border: none;
  cursor: pointer;
}

img {
  max-width: 100%;
}

.container {
  max-width: 61.75rem;
  width: 90%;
  margin: 0 auto;
}

.grid {
  display: grid;
}

.button-primary {
  padding: 0.5rem 1rem;
  border-radius: 2rem;
  background-color: #f4ce14;
  color: #495e57;
  font-weight: bold;
  border: 2px solid #f4ce14;
}

.button-primary:hover,
.button-primary:focus {
  background-color: #495e57;
  border-color: #495e57;
  color: #ffffff;
}
/* Center the confirmation message */
.confirmed-reservation {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  height: 100vh; /* Make it take the full height of the screen */
  text-align: center;
  gap: 1rem; /* Adds spacing between elements */
  padding-top: 30vh; /* Moves the content down (Adjust as needed) */
}

.confirmed-reservation svg {
  color: #4CAF50; /* Green check icon */
}

.confirmed-reservation h2 {
  font-size: 1.8rem;
  color: #333;
}

.confirmed-reservation p {
  font-size: 1.2rem;
  color: #666;
  max-width: 500px; /* Restrict width for better readability */
}
